      <Tab.Navigator
        screenOptions={{
          headerShown: false,
          tabBarHideOnKeyboard: true,
          tabBarShowLabel: false,
        }}
      >

        <Tab.Screen
          name='Usuario'
          component={Usuario}
          options={{
            tabBarIcon: ({ color, size }) => { return <Feather name='file-text' color={color} size={size} /> }
          }} />

        <Tab.Screen
          name='Peca'
          component={Peca}
          options={{
            tabBarIcon: ({ color, size }) => { return <Feather name='tool' color={color} size={size} /> }
          }} />


      </Tab.Navigator>
  );
}
